const TransactionsTable = ({ month }) => {
    const [transactions, setTransactions] = useState([]);
    const [totalCount, setTotalCount] = useState(0); // To keep track of total transactions for pagination
    const [page, setPage] = useState(1); // Current page number
    const [perPage] = useState(10); // Transactions per page
    const [search, setSearch] = useState('');

    useEffect(() => {
        const fetchAndSetTransactions = async () => {
            const response = await fetchTransactions({ month, search, page, perPage });
            setTransactions(response.transactions);
            setTotalCount(response.totalCount); // Update total count for pagination
        };

        fetchAndSetTransactions();
    }, [month, search, page, perPage]);

    const handleNextPage = () => {
        if (page * perPage < totalCount) {
            setPage(prevPage => prevPage + 1);
        }
    };

    const handlePreviousPage = () => {
        if (page > 1) {
            setPage(prevPage => prevPage - 1);
        }
    };

    const totalPages = Math.ceil(totalCount / perPage); // Calculate total pages

    return (
        <div>
            <input
                type="text"
                placeholder="Search transactions"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.length > 0 ? (
                        transactions.map((t) => (
                            <tr key={t.id}>
                                <td>{t.id}</td>
                                <td>{t.title}</td>
                                <td>{t.description}</td>
                                <td>{t.price}</td>
                                <td>{t.category}</td>
                                <td>{t.sold ? 'Yes' : 'No'}</td>
                                <td>
                                    <img src={t.image} alt={t.title} style={{ width: '50px', height: '50px' }} />
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7" style={{ textAlign: 'center' }}>No transactions found</td>
                        </tr>
                    )}
                </tbody>
            </table>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '20px' }}>
                <div>
                    <span>
                        Page {page} of {totalPages}
                    </span>
                </div>
                <div className="pagination-container">
                    <span 
                        onClick={handlePreviousPage} 
                        style={{ cursor: page === 1 ? 'not-allowed' : 'pointer', color: page === 1 ? '#ccc' : 'blue' }}
                    >
                        Previous
                    </span>
                    <span 
                        onClick={handleNextPage} 
                        style={{ cursor: page * perPage >= totalCount ? 'not-allowed' : 'pointer', color: page * perPage >= totalCount ? '#ccc' : 'blue' }}
                    >
                        Next
                    </span>
                </div>
            </div>
        </div>
    );
};

export default TransactionsTable;
