import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const fetchTransactions = async (month) => {
    if (!month) { // Check if month is provided
        console.error("Month is required for fetching transactions.");
        throw new Error("Month is required"); // Prevent making the request
    }

    try {
        const response = await axios.get(`${API_URL}/transactions`, { params: { month } });
        return response.data; // Adjust based on the actual API response structure
    } catch (error) {
        if (error.response) {
            console.error("Error fetching transactions:", error.response.data);
            throw new Error(error.response.data.message || "Error fetching transactions");
        } else {
            console.error("Error fetching transactions:", error);
            throw new Error("Error fetching transactions: " + error.message);
        }
    }
};

export const fetchStatistics = async (month) => {
    if (!month) { // Check if month is provided
        console.error("Month is required for fetching statistics.");
        throw new Error("Month is required");
    }

    try {
        const response = await axios.get(`${API_URL}/statistics`, { params: { month } });
        return response.data; // Adjust based on the actual API response structure
    } catch (error) {
        if (error.response) {
            console.error("Error fetching statistics:", error.response.data);
            throw new Error(error.response.data.message || "Error fetching statistics");
        } else {
            console.error("Error fetching statistics:", error);
            throw new Error("Error fetching statistics: " + error.message);
        }
    }
};

export const fetchBarChartData = async (month) => {
    if (!month) {
        console.error("Month is required for fetching bar chart data.");
        throw new Error("Month is required");
    }

    try {
        const response = await axios.get(`${API_URL}/bar-chart-data`, { params: { month } });
        console.log("API response data:", response.data);
        return response.data; // Adjust based on the actual API response structure
    } catch (error) {
        if (error.response) {
            console.error("Error fetching bar chart data:", error.response.data);
            throw new Error(error.response.data.message || "Error fetching bar chart data");
        } else {
            console.error("Error fetching bar chart data:", error);
            throw new Error("Error fetching bar chart data: " + error.message);
        }
    }
};
