// api/transactionsApi.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const fetchTransactions = async (params) => {
    if (!params || Object.keys(params).length === 0) {
        console.error("No parameters provided for fetching transactions.");
        throw new Error("No parameters provided"); // Prevent making the request
    }

    // Log parameters being sent to the API for better debugging
    console.log("Fetching transactions with params:", params);

    try {
        const response = await axios.get(`${API_URL}/transactions`, { params });

        // Check if the response is empty or does not have the expected structure
        if (!response.data || typeof response.data !== 'object') {
            console.error("Unexpected response structure:", response.data);
            throw new Error("Unexpected response structure");
        }

        return response.data; // Adjust based on the actual API response structure
    } catch (error) {
        if (error.response) {
            console.error("Error fetching transactions:", error.response.data);
            throw new Error(error.response.data.message || "Error fetching transactions");
        } else {
            console.error("Error fetching transactions:", error);
            throw error;
        }
    }
};


export const fetchStatistics = async (month) => {
    if (!month) {
        console.error("Month is required for fetching statistics.");
        throw new Error("Month is required");
    }

    console.log("Fetching statistics for month:", month); // Log the month being fetched

    try {
        const response = await axios.get(`${API_URL}/transactions/statistics`, { params: { month } });
        return response.data; // Adjust based on the actual API response structure
    } catch (error) {
        console.error("Error fetching statistics:", error);
        throw error;
    }
};


export const fetchBarChartData = async (month) => {
    if (!month) {
        console.error("Month is required for fetching bar chart data.");
        throw new Error("Month is required");
    }

    const monthIndex = isNaN(month) 
        ? new Date(Date.parse(month + " 1, 2021")).getMonth() + 1 
        : month;

    try {
        // Updated the API URL to match the defined routes
        const response = await axios.get(`${API_URL}/transactions/bar-chart`, { params: { month: monthIndex } });
        console.log("API response data:", response.data);
        return response.data;
    } catch (error) {
        if (error.response) {
            console.error("Error fetching bar chart data:", error.response.data);
            throw new Error(error.response.data.message || "Error fetching bar chart data");
        } else {
            console.error("Error fetching bar chart data:", error);
            throw error;
        }
    }
};
